    def log_in(self):
        user_names = ['Elubai', 'Zhansaya', 'Aygerim']
        Email_check = re.compile(r'([A-Za-z0-9]+[.-_])*[A-Za-z0-9]+@[A-Za-z0-9-]+(\.[A-Z|a-z]{2,})+')
        password_check = re.compile(r'^.*(?=.{8,10})(?=.*[a-zA-Z])(?=.*?[A-Z])(?=.*\d)[a-zA-Z0-9!@Â£$%^&*()_+={}?:~\[\]]+$')
        if self.username.text() in user_names:
            if re.fullmatch(Email_check, self.Email.text()):
                if re.match(r'[1-9]{1}[0-9]{9}', self.Phone.text()) and len(self.Phone.text()) == 11:
                    if re.fullmatch(password_check, self.Password.text()):
                        self.Form = QtWidgets.QWidget()
                        self.ui = Ui_Comp_app()
                        self.ui.setupUi(self.Form)
                        self.Form.show()
                    else:
                        self.username.setText('Incorrect password')
                else:
                    self.Phone.setText('Incorrect phone number')
            else:
                self.Email.setText('Incorrect email')
        else:
            self.username.setText('Incorrect name')




